import React, { useRef, useState, useEffect } from "react";
import Li_fournisseurs from "./li_fournisseurs";

function Options(props) {
    return (
        <option key={props.fournisseur.identreprise} value={props.fournisseur.identreprise}  >
                                {props.fournisseur.nom}
                            </option>
    );
}



function Add_fournisseur(props) {
    const [all_fournisseurs, setAll_fournisseurs] = useState(props.all_fournisseurs);
    const [selected_fournisseurs, setSelected_fournisseurs] = useState([]);


    function Addfournisseur() {
        var selected_value = document.getElementsByClassName("addF")[0].selectedOptions[0].value;


        const f = props.all_fournisseurs.find(({ identreprise }) => identreprise === parseInt(selected_value));
        
        const all=props.all_fournisseurs;
        all.splice(props.all_fournisseurs.indexOf(f), 1); 

        setAll_fournisseurs(all);
        setSelected_fournisseurs([...selected_fournisseurs,f]);

    }


    return (
        <div>

        <div className="card text-start">
            <div className="card-body">
                <h4 className="card-title">Fournisseurs</h4>
                <div className="mb-3">
                    <select className="form-select addF" name="add_fournisseur" id="" value={props.fourniss} >
                        

                        {
                            props.all_fournisseurs.map((element,index)=>(
                                <Options fournisseur={element} key={index}/>
                            ))
                        }
                    </select>
                </div>
                <button type="button"  onClick={Addfournisseur} className="btn btn-secondary">Ajouter</button>
            </div>
        </div>
            <ul className="list-group" style={{"marginTop":"20px"}}>

            {
                selected_fournisseurs.map((element,index)=>( <Li_fournisseurs fournisseur_demande={props.fournisseur_demande} fournisseurs={element} index={index} key={index} />)
                
                )
            }
            </ul>
        </div>
    );
}

export default Add_fournisseur;
