import React, { useRef, useState, useEffect } from "react";
import Header from "../../Template/Departement/Header";
import Card from "./Card";

const DemandeAchat = () => {
  const [cards, setCards] = useState([]);
  const [demandeAchat, setDemandeAchat] = useState([]);
  const rubrique = useRef([]);
  const nomarticle = useRef([]);
  const quantite = useRef([]);
  const unite = useRef([]);
  const description = useRef([]);

  class DemandeAchat {
    idrubrique;
    idarticle;
    nomarticle;
    quantite;
    unite;
    description;
  }

  const handleClick = (event) => {
    setCards([...cards, ""]);
  };

  useEffect(() => {
    var content = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    };

    content.body = JSON.stringify({ article: demandeAchat });
    console.log(content);
    // if (demandeAchat.length !== 0) {
    //   fetch("http://127.0.0.1:3000/demanderAchatv", content).then(
    //     (response) => {
    //       if (response.status === 400) {
    //         alert("Erreur lors du demande d'Achat");
    //       }
    //     }
    //   );
    // }
  }, [demandeAchat]);

  const handleForm = (event) => {
    var value = [];
    for (let i = 0; i < description.current.length; i++) {
      var newDemandeAchat = new DemandeAchat();
      newDemandeAchat.rubrique = rubrique.current[i].value;
      newDemandeAchat.quantite = quantite.current[i].value;
      newDemandeAchat.unite = unite.current[i].value;
      newDemandeAchat.description = description.current[i].value;
      newDemandeAchat.nomarticle = nomarticle.current[i].value;
      console.log(nomarticle.current[i].value);
      value.push(newDemandeAchat);
    }
    setDemandeAchat([...demandeAchat, value]);
    console.log("vfvbf");
    console.log(value);
  };

  return (
    <div>
      <Header />
      <div className="page-section">
        <div className="container">
          <div className="row justify-content-center">
            <div className="text-center">
              <h1>
                <span className="text-secondary">Demande D'achats</span>
              </h1>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-3">
              <button
                className="form-submit btn btn-primary"
                type="button"
                style={{ backgroundColor: "green", borderColor: "green" }}
                onClick={handleClick}
              >
                Ajouter
              </button>
            </div>
          </div>

          <form className="form-group">
            <div className="row">
              {cards.map((element, index) => (
                <Card
                  key={index}
                  index={index}
                  rubrique={rubrique}
                  quantite={quantite}
                  unite={unite}
                  description={description}
                  nomarticle={nomarticle}
                />
              ))}
            </div>

            <div className="row justify-content-center">
              <button
                className="form-submit btn btn-primary"
                type="button"
                onClick={handleForm}
              >
                Confirmer
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DemandeAchat;
