import React, { useEffect, useState } from "react";
import { useLocation } from "react-router";
import Header from "../../Template/Appro/Header";
const EmailInterface = () =>{
    // receiving the data from the initial location
    const location = useLocation();

    console.log(location.state);

    return(
        // element being the name of the element you apas
        <div>
            <div id="clientInfos">
                <h2>Client:</h2>
                <p>{location.state.json.theAppro.nom}</p>
                <p>{location.state.json.theAppro.prenom}</p>
            </div>

            <div id="fournisseursInfos">
                <h2>Fournisseurs</h2>
                {location.state.json.fournisseurs_itSelf.map((fournisseur)=>(
                    <div>
                        <p>Nom de la société fournisseur {fournisseur.nom}</p>
                        <p>Email de la société fournisseur {fournisseur.email}</p>
                        <p>Adresse de l'adresse fournisseur {fournisseur.email}</p>
                        <hr/>
                    </div>
                ))}
            </div>

                
            <div id="contentEmail">
                <h2>Objet : Demande de la facture de pro forma</h2>
                <h3>[Madame / Monsieur] [le / la] responsable </h3>
                <p>
                    Suite à nos différents échanges, je vous demande de bien vouloir m'adresser une facture proformat , récapitulant les conditions de la vente envisagée , dans les plus brefs délais.
                    Dans l'attente de votre retour, veuillez agréer, [Madame / Monsieur] [le / la] responsable , l'expresionn de mes sentiments respectueux.
                </p>
            </div>

            <div id="emailFooter">
                <p>{location.state.json.theAppro.nom}  {location.state.json.theAppro.prenom}</p>
            </div>

        </div>
    );

}
export default EmailInterface;
