import { useEffect,useRef,useState} from "react";
import { useParams } from "react-router";
function LineBonCommande(props){

    return (
      <div>
        <div className="row">
            <div className="col-sm-1">
                <div className="form-group mb-3">
                    <input  className="form-control" type="hidden" value={props.iddetails_proformat_fournisseur} ref={props.detail_pf} disabled/>
                    <label className="form-label">article(pf)</label>
                    <input  className="form-control" type="text" value={props.nomarticle} disabled/>
                </div>
            </div>
            <div className="col-sm-1">
                <div className="form-group mb-3"><label className="form-label">quantite</label><input className="form-control" type="number" defaultValue={props.qtt} ref={props.quantite}/></div>
            </div>
            <div className="col-sm-1">
                <div className="form-group mb-3"><label className="form-label">prix unitaire</label><input className="form-control" type="text" value={props.pu} ref={props.ht} disabled/></div>
            </div>
            <div className="col-sm-1">
                <div className="form-group mb-3"><label className="form-label">periode valide</label><input className="form-control" type="date" ref={props.periode}/></div>
            </div>
        </div>
  
      </div>
    );
  }  
  function BonCommandeFournisseur(){
    const {idproforma} = useParams();
    const iddetails_bon_commande_fournisseur=0;
    const detail_pf=useRef([]);
    const quantite=useRef([]);
    const hors_taxe=useRef([]);
    const tva=useRef([]);
    const ttc=useRef([]);
    const montant_lettre=useRef([]);
    const periode_valide=useRef([]);

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    useEffect(() => {
      
      fetch("http://127.0.0.1:3000/proformaInfo?proformat_id="+idproforma)
        .then(res => res.json())
        .then(
          (result) => {
  
            setIsLoaded(true);
            setItems(result);
  
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            
            setIsLoaded(true);
            setError(error);
          }
        )
    }, [])
    
    var elements = [];
    items.proformainfo?.map((element)=>{
      const line = {"iddetails_proformat_fournisseur":element.iddetails_proformat_fournisseur,"nomarticle":element.nomarticle,"qtt":element.qtt,"pu_30":element.pu_30};
      elements.push(line);
    })
  
    function handleDetail_pf(el){
        if(el && !detail_pf.current.includes(el)){
          detail_pf.current.push(el);
        }
      };
      function handleQuantite(el){
        if(el && !quantite.current.includes(el)){
          quantite.current.push(el);
        }
      };
      function handleHors_taxe(el){
        if(el && !hors_taxe.current.includes(el)){
          hors_taxe.current.push(el);
        }
      };
      function handleTva(el){
        if(el && !tva.current.includes(el)){
          tva.current.push(el);
        }
      };
      function handleTtc(el){
        if(el && !ttc.current.includes(el)){
          ttc.current.push(el);
        }
      };
      function handleMontant_lettre(el){
        if(el && !montant_lettre.current.includes(el)){
          montant_lettre.current.push(el);
        }
      };
      function handlePeriode_valide(el){
        if(el && !periode_valide.current.includes(el)){
          periode_valide.current.push(el);
        }
      };

    function handleEvent(event){
        const details = [];  
        const data = {}
        for (let index = 0; index < quantite.current.length; index++) {
               const line = {iddetails_bon_commande_fournisseur:iddetails_bon_commande_fournisseur,details_proformat_fournisseur_id:detail_pf.current[index].value,qtt:quantite.current[index].value,ht:hors_taxe.current[index].value,periode_valide:periode_valide.current[index].value};
               console.log(line);
               details.push(line);
           }
        
        data.list = details;   
        event.preventDefault();

           
      var content= {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        }
    }
    
    content.body=JSON.stringify(data)
    fetch('http://127.0.0.1:3000/bdcfournisseur', content).then(response => {
        if(response.status===400){
            alert("Failed")
        }else
            alert("Success")
    })
    
    };

    return (
      <div>
        <h1>Insertion de bon de commande</h1>
        <div>
        {
              elements.map((el,index)=>{
                  return(
                    // <ProformaBody key={index} idarticle={el.value} article_label={el.text} articles_id={handleArticle} prix_unit={handlePrix} qualite={handleQualite} quantite={handleQuantite} qtt={el.quantite} qlt={el.qualite}/>                        
                    <LineBonCommande key={index} iddetails_proformat_fournisseur={el.iddetails_proformat_fournisseur} nomarticle={el.nomarticle} qtt={el.qtt} detail_pf={handleDetail_pf} quantite={handleQuantite} pu={el.pu_30} ht={handleHors_taxe}  periode={handlePeriode_valide}/>
                    // <LineBonCommande key={index} nomarticle={el.nomarticle} qtt={el.qtt} detail_pf={handleDetail_pf}/>

                    )
              })
            }
           
        </div>
        <div>
            <div className="col-sm-3">
              <input className="btn btn-primary" type="submit" value="valider" onClick={handleEvent}/>
            </div>
        </div>
      </div>
    );
  }  
  
export default BonCommandeFournisseur
