import React, { useRef } from 'react'

const Login = () => {
    const email = useRef(null);
    const pwd = useRef(null);

    function setInfo_Appro(data){
        sessionStorage.setItem("Appro",JSON.stringify(data))
    }

    const HandleLogin=(event) =>{
        event.preventDefault();
        
        var content= {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            }
        }

        content.body=JSON.stringify({email:email.current.value,pwd:pwd.current.value})
        fetch('http://127.0.0.1:3000/loginAppro', content).then(response => {
            if(response.status===400){
                alert("Mot de passe non identifier")
            }else
                return response.json()
        })
        .then(json => { setInfo_Appro(json) })
        
    }

  return (
    <div className="parent clearfix">
        <div className="bg-illustration">
            <img src="https://i.ibb.co/Pcg0Pk1/logo.png" alt="logo" />
            <div className="burger-btn">
                <span></span>
                <span></span>
                <span></span>
            </div>
        </div>
        <div className="login">
            <div className="container">
                <h1>Login to access to<br />your account</h1>
    
                <div className="login-form">
                    <form action="submit"  method="post">
                        <input ref={email} type="email" placeholder="E-mail Address" />
                        <input ref={pwd} type="password" placeholder="Password" />
                        <button type="submit" onClick={HandleLogin}>LOG-IN</button>
                    </form>
                </div>
            </div>
        </div>
    </div>
  )
}

export default Login
