function checkAll() {
    const checkboxes = document.getElementsByClassName("form-check-input");
    for (let i = 0; i < checkboxes.length; i++) {
        checkboxes[i].checked = true;

    }
}
function uncheckAll() {
    const checkboxes = document.getElementsByClassName("form-check-input");
    for (let i = 0; i < checkboxes.length; i++) {
        checkboxes[i].checked = false;

    }
}
function Checkbox_btn() {
    return (
        <div>

            <button type="button" onClick={checkAll} name="" id="" className="btn btn-secondary">select all</button>
            <button type="button" name="" onClick={uncheckAll} id="" className="btn btn-secondary">select none</button>
        </div>
    );
}

export default Checkbox_btn;
