import React from "react";

const DataList = ({ data, index, nomarticle,rubrique }) => {
  var dataList = [];
  console.log(rubrique);
  if(rubrique!="")
  {data=data.filter((element)=>
    element.idrubrique==rubrique
  )}
  data.map((element) =>
    dataList.push({
      id: element.idarticle,
      value: element.nom_article,
    })
  );


  console.log(data);
  const button = () => {};

  return (
    <div key={index}>
      <label htmlFor="art">Article</label>
      <input id="art" className="form-control"
        type="text"
        list="datalist"
        onChange={button}
        ref={(element) => {
          nomarticle.current[index] = element;
        }}
      />
      <datalist id="datalist">
        {dataList
        .map((item, key) => (
          <option key={key} value={item.value}>
            {item.value}
          </option>
        ))}
      </datalist>
    </div>
  );
};

export default DataList;
