import { useState } from "react";

function Tr_checkBox(props) {

    const [checked,setChecked]=useState(false);

    function handleCheck(){
        setChecked(!checked);
    }
    return (

        <tr className="" onClick={handleCheck}>
            <td scope="row">{props.article_fixed.nomrubrique}</td>
            <td>{props.article_fixed.nom_article}</td>
            <td>{props.article_fixed.qtt}</td>
            <td>{props.article_fixed.qualite}</td>
            <td><input onChange={handleCheck} className="form-check-input" name="article_fixed_id" ref={(element) => {
                  props.article_demande.current[props.index] = element;
                }}  checked={checked} type="checkbox" value={props.article_fixed.idarticle_fixed} key={props.article_fixed.idarticle_fixed} /></td>
        </tr>


    );
}

export default Tr_checkBox;
