import React from "react";

const List = ({ listes, rubriques, articles, listMethode }) => {
  const header = [
    "Rubrique",
    "Article",
    "Quantite",
    "Prix Unitaire",
    "Qualite",
    "Methode",
  ];

  return (
    <table className="table">
      <thead>
        <tr>
          {header.map((element, index) => (
            <th key={index}>{element}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {listes.map((element, index) => (
          <tr key={index}>
            <td>
              {
                rubriques.filter(
                  (rubrique) => rubrique.idrubrique == element.rubrique
                )[0].nomrubrique
              }
            </td>
            <td>
              {
                articles.filter(
                  (article) => article.idarticle_fournisseur == element.article
                )[0].nomarticle
              }
            </td>
            <td>{element.qtt}</td>
            <td>{element.pu_30}</td>
            <td>{element.qualite}</td>
            <td>
              {
                listMethode.filter(
                  (methode) => methode.idmethode == element.methode
                )[0].nom_methode
              }
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default List;
