import { Fragment ,useState,useEffect} from "react";
import { useParams } from "react-router";
import { Link } from "react-router-dom";

const ProformatDetailsBcmd = () => {
    const params = useParams();
    const [details, setDetails] = useState([]);


    useEffect(() => {
        var content = {
            method : "GET",
            headers : {
                "Content-Type": "application/json"
            },
        };
  
        fetch("http://127.0.0.1:3000/getDpfBcmd/"+params.idProformat, content)
        .then((response)=>{
            return response.json();
        })
        .then((json)=>{
            console.log(json);
            setDetails(json.allWithBcmd)
            // console.log(allProformat);
        })
    }, [])
    
    return(
        <Fragment>
            <table>
                <thead>
                    <tr>
                        <th>Qtt</th>
                        <th>Prix avec augmentation 30%</th>
                        <th>Qualite</th>
                        <th>Montant HT</th>
                        <th>Montant TVA</th>
                        <th>Montant TTC</th>
                    </tr>
                </thead>
                <tbody>
                {details.map( (element,index) =>(
                    <tr key={index}>
                        <td></td>
                    </tr>
                ))}
                </tbody>
            </table>
        </Fragment>
    );
}

export default ProformatDetailsBcmd;
