import { useEffect } from "react";
import { useState } from "react";

const Analyse = () => {
  const filtre = ["Fournisseur", "Produit", "Par semaine", "Bon commande"];
  const [critere, setCritere] = useState(null);

  useEffect(() => {
    if (critere != null) {
      console.log(critere);
    }
  }, [critere]);

  return (
    <div>
      <h1>Analyse chiffre d'affaire sur l'achat</h1>
      <br />

      <h2>Filtre selon critere</h2>
      <div className="col-sm-2">
        <select
          className="form-control"
          onClick={(e) => setCritere(e.target.value)}
        >
          {filtre.map((e, index) => (
            <option key={index} value={e}>
              {e}
            </option>
          ))}
        </select>
      </div>

      <div>
        <table className="table">
          <thead>
            <tr>
              <th>Produit</th>
              <th>Chiffre d'affaire</th>
            </tr>
          </thead>
          <tbody>
            <tr></tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Analyse;
