import { useEffect ,useState} from "react"
import Header from "../../../Template/Fournisseur/Header";

const HistoriqueStock = () => {
    const [allDetails, setAllDetails] = useState([]);
    const [currentStokCmup, setCurrentStokCmup] = useState([]);
    const [currentStock, setCurrentStock] = useState([]);
    useEffect(() => {
        let content = {
            method: "GET",
          };
      
          fetch("http://127.0.0.1:3000/getAllTransaction",content)
          .then( (response) => {
                return response.json()
          })
          .then( (json) => {
              console.log(json.allDetails);
                setAllDetails(json.allDetails);
                setCurrentStock(json.currentStock);
                setCurrentStokCmup(json.currentStockCmup);
          })
    }, [])
    

    if(allDetails===undefined)
        return <p>Loading</p>

    return(
        <>
        <Header>
            <h2>Historique des transactions</h2>
            <table border={1}>
                <thead>
                    <tr>
                        <th>Nom article</th>
                        <th>Qtt</th>
                        <th>Prix unitaire</th>
                    </tr>    
                </thead>   

                <tbody>
                    {allDetails.map( (element,index) =>  (
                    <tr key={index}>
                        <td>{element.nomarticle}</td>
                        <td>{element.out_qtt}</td>
                        <td>{element.pu}</td>
                    </tr>
                    ))}
                </tbody>
            </table>

            <h2>Stock actuel</h2>
            <table border={1}>
                <thead>
                    <tr>
                        <th>Nom article</th>
                        <th>Qtt</th>
                        <th>Valeur</th>
                        <th>Méthode de transaction</th>
                    </tr>    
                </thead>   
                
                <tbody>
                    {currentStock.map( (element,index) => (
                        <tr key={index}>
                        <td>{element.nomarticle}</td>
                        <td>{element.remaining_qtt}</td>
                        <td>{element.prix_total}</td>
                        <td>{element.nom_methode}</td>
                    </tr>
                    ))}
                    {currentStokCmup.map( (element,index) => (
                        <tr key={index}>
                        <td>{element.nomarticle}</td>
                        <td>{element.remaining_qtt}</td>
                        <td>{element.prix_total}</td>
                        <td>{element.nom_methode}</td>
                    </tr>
                    ))}
                </tbody>
            </table>
            </Header>
        </>
    );
}

export default HistoriqueStock;
