import { useState,useEffect } from "react";
import Tr_ProformaF from "./Tr_ProformaF";
import Header from "../../Template/Fournisseur/Header";

function PfToFacture() {

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch("http://127.0.0.1:3000/all_proformasfournisseur")
      .then(res => res.json())
      .then(
        (result) => {

          setIsLoaded(true);
          setItems(result);

        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
      console.log(items.proformas);
    return (
      <div>
      
      <Header />
      <table className="table">
          <thead>
              <tr>
                  <th scope="col">#</th>
                  <th scope="col">Acompte</th>
                  <th scope="col">Date de livraison</th>
                  <th scope="col">Lieu de livraison</th>
              </tr>
          </thead>
          <tbody>
              { items.proformas?.map( (d,index) =>(
                <Tr_ProformaF link={""} key={index} proforma={d}></Tr_ProformaF>
                ))}

            </tbody>
          </table>

      
      </div>
    );
  }
}

//export default App;
export default PfToFacture;
