import React, { useEffect, useState } from "react";
import Header from "../../Template/Appro/Header";
import Table from "./Table";

const Validation = () => {
  const [allDepartement, setAllDepartement] = useState([]);
  const [listeDemandeAchat, setListeDemandeAchat] = useState([]);
  const [departement, setDepartement] = useState(" ");
  const header = [
    "Departement",
    "Produit",
    "Quantite",
    "Unite",
    "Description",
    "Date",
  ];

  useEffect(() => {
    var content = {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    };

    fetch("http://127.0.0.1:3000/giveDataValidationDemanderAchat", content)
      .then((response) => {
        if (response.status === 400) {
          alert("Mot de passe non identifier");
        } else return response.json();
      })
      .then((json) => {
        setAllDepartement(json.allDepartement);
        setListeDemandeAchat(json.listeDemandeAchatValider);
      });
  }, []);

  const handlebutton = (event) => {
    setDepartement(event.target.value);
  };

  return (
    <div className="container">
      <Header />
      <div>
        <div className="row justify-content-center">
          <h1 className="text-center">Demandes</h1>
        </div>

        <div className="row">
          <select className="form-control" onClick={handlebutton}>
            {allDepartement.map((element, index) => (
              <option value={element.nomdepartement} key={index}>
                {element.nomdepartement}
              </option>
            ))}
            <option value=" ">Tous</option>
          </select>
        </div>

        <div className="">
          <div className="row justify-content-center align-items-center h-100">
            <div className="col-lg">
              <div className="row">
                <Table
                  header={header}
                  listDemande={listeDemandeAchat}
                  departement={departement}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Validation;
