import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import Header from "../../../Template/Appro/Header";

const Facture = () => {
    const params = useParams();
    const [generalInfos, setGeneralInfos] = useState();
    const [allDetails, setAllDetails] = useState([]);
    const [time_location,setTime_location] = useState()
    const [corporateInfos, setCorporateInfos] = useState()
    const [today, setToday] = useState();

    
    useEffect(() => {
        let content = {
          method: "GET",
        };
    
        fetch("http://127.0.0.1:3000/getFacturePreview/" +params.idProformat+"/2",content)
          .then((response) => {
            return response.json();
          })
          .then((json) => {
            //   console.log(json.generalInfos.sum_ht);
            setGeneralInfos(json.generalInfos);
            setAllDetails(json.allDetails);
            setTime_location(json.time_location);
            setCorporateInfos(json.corporateInfos)
    
            let date = new Date();
            let bref =
              date.getFullYear() +
              "-" +
              (date.getMonth() + 1) +
              "-" +
              date.getDate();
            setToday(bref);
          });
      }, []);

      const saveFacture = () => {
        let facture = {
            proformat_id : params.idProformat,
            date_creation : today,
            montant_total_ht : generalInfos.sum_ttc,
            montant_total_tva : generalInfos.sum_tva,
            montant_total_ttc : generalInfos.sum_ttc,
            montant_lettre : generalInfos.sum_in_letter
        }

        let facture_details = []
        allDetails.map( (element,index) =>{
            facture_details.push({
                qtt : element.qtt_livre,
                ht : element.ht,
                tva : element.tva,
                ttc : element.ttc,
                stock_id : element.stock_id
            })
        })

        let coreData = {
            facture : facture,
            facture_details : facture_details
        }

        let content = {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
          };
          content.body = JSON.stringify(coreData);
        fetch("http://127.0.0.1:3000/saveFacture",content)
      }

      if(generalInfos===undefined || allDetails===undefined){
          return <p>Page loading</p>
      }

    return(
        <>
            <Header  />
            <div className="container">
        <div className="row justify-content-center">
          <h3>Aperçu de facture</h3>
          <hr />
        </div>

        <div className="row">
          <div className="col-lg">
            <h5>
              <strong>Nom de la Societe: </strong>
              {corporateInfos.nom}
            </h5>
            <h5>
              <strong>Adresse: </strong>
              {corporateInfos.adresse}
            </h5>
            <h5>
              <strong>Telephone: </strong>
              {corporateInfos.tel}
            </h5>
          </div>
        </div>

        <div className="row"></div>

        <div className="row align-items-center justify-content-center">
          <div className="col-lg-4">
            <h5>
              <strong>Date aujourd'hui: </strong>
              {today}
            </h5>
          </div>
          <div className="col-lg-4"></div>
          <div className="col-lg-4">
          </div>
        </div>

        <div className="">
          <div className="row justify-content-center align-items-center h-100">
            <div className="col-lg">
              <div className="row">
                <table className="table" border="1">
                  <thead>
                    <tr>
                      <th>Nom article</th>
                      <th>Quantite</th>
                      <th>PU</th>
                      <th>Montant ht</th>
                      <th>Montant TVA</th>
                      <th>Montant TTC</th>
                      <th>Date Livraison</th>
                    </tr>
                  </thead>

                  <tbody>
                    {allDetails.map((detail, index) => (
                      <tr key={index}>
                        <td>{detail.nomarticle}</td>
                        <td>{detail.qtt_livre}</td>
                        <td>{detail.pu}</td>
                        <td>{detail.ht}</td>
                        <td>{detail.tva}</td>
                        <td>{detail.ttc}</td>
                        <td>{detail.date_livraison}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-lg">
            <h5>
              <strong>Mode de Livraison: </strong>
              {time_location.nommode_livraison}
            </h5>
            <h5>
                <strong>Lieu de livraison: </strong>
                {time_location.lieu_livraison}
            </h5>
          </div>
        </div>

        <hr />

        <div className="row">
            <div className="col-lg">
                <h5><strong>Montant total HT:</strong>{generalInfos.sum_ht}</h5>
                <h5><strong>Montant total TVA:</strong>{generalInfos.sum_tva}</h5>
                <h5><strong>Montant total TTC:</strong>{generalInfos.sum_ttc}</h5>
                <h5><strong>Montant en lettre:</strong>{generalInfos.sum_in_letter}</h5>
            </div>
        </div>

        <button onClick={saveFacture}>Générer</button>
      </div>

        </>
    );
}
export default Facture;
