import { Fragment ,useState,useEffect, useRef} from "react";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import Header from "../../Template/Appro/Header";

const ProformatDetailsBcmd = () => {
    const params = useParams();
    const [details, setDetails] = useState([]);
    const checkBoxesRefs = useRef([]);

    const generateBonSortie = () => {
        console.log(checkBoxesRefs);
    }

    const generateInputField =() => {
        console.log("kanefa");
    }
    
    useEffect(() => {
        let content = {
            method : "GET",
            headers : {
                "Content-Type": "application/json"
            },
        };
  
        fetch("http://127.0.0.1:3000/getDpfBcmd/"+params.idProformat, content)
        .then((response)=>{
            return response.json();
        })
        .then((json)=>{
            console.log(json);
            setDetails(json.allWithBcmd)
        })
    }, [])
    
    return(
        <Fragment>
            <Header/>
            <table border='1'>
                <thead>
                    <tr>
                        <th>Qtt</th>
                        <th>Prix avec augmentation 30%</th>
                        <th>Qualite</th>
                        <th>Montant HT</th>
                        <th>Montant TVA</th>
                        <th>Montant TTC</th>
                        <th>Quantite a sortir</th>
                    </tr>
                </thead>
                <tbody>
                {details.map( (element,index) =>(
                    <tr key={index}>
                        <td>{element.qtt}</td>
                        <td>{element.pu_30}</td>
                        <td>{element.qualite}</td>
                        <td>{element.ht}</td>
                        <td>{element.tva}</td>
                        <td>{element.ttc}</td>
                        {/* <td><input type="number" name="out_qtt"/></td> */}
                        <td><input type="checkbox" onChange={generateInputField} ref={ el => (checkBoxesRefs.current[index] = el) } name="checkBoxes" /></td>
                    </tr>
                ))}
                </tbody>
            </table>

            <button onClick={generateBonSortie}>Valider bon sortie</button>
        </Fragment>
    );
}

export default ProformatDetailsBcmd;
