import { Fragment ,useState,useEffect, useRef} from "react";
import { useNavigate, useParams } from "react-router";
import Header from "../../../Template/Appro/Header";

const PFDBonLivraison = () => {
    const navigate = useNavigate();
    const params = useParams();
    const [details, setDetails] = useState([]);
    
    const checkBoxesRefs = useRef([]);
    const dateInputsRefs = useRef([]);
    const outQttInputsRefs = useRef([]);

    class BonLivraison{
        constructor(a,b,c){
            this.details_bon_commande_fournisseur_id = a;
            this.qtt_livre = b;
            this.date_livraison = c;
        }

        details_bon_commande_fournisseur_id;
        out_qtt;
        date_livraison;
    }


    //     # Il faut savoir que la qtt qu'il essaye de sortir ne dépasse pas le stock
    const generateBonLivraison = () => {
        let bon_livraison_arrays = [];
        checkBoxesRefs.current.map( (element,index) => {
            if(element.checked)
            bon_livraison_arrays.push(new BonLivraison(details[index].iddetails_bon_commande_fournisseur,+outQttInputsRefs.current[index].value))
        });
        
        let content = {
            method : "POST",
            headers : {
                "Content-Type": "application/json"
            },
        };

        content.body = JSON.stringify(bon_sortie_arrays);
        
        fetch("http://127.0.0.1:3000/generateBonSortie",content)
        .then(response => {
            console.log(response.status);
            if(response.status!==200){
                throw new Error(response);
            }

            else if(response.status===200)
                return response.json();
        })
        .then(data => {
            navigate('/menu');  
        })
        .catch(error => {
            alert("La quantite que vous essayez de sortir est superieur a la commande")
        })
    }

    const generateInputField =(index) => {
        let copie_outQttInputs = [...outQttInputs];
        copie_outQttInputs[index] = !copie_outQttInputs[index];
        setOutQttInputs(copie_outQttInputs)
    }
    
    useEffect(() => {
        let content = {
            method : "GET",
            headers : {
                "Content-Type": "application/json"
            },
        };
  
        fetch("http://127.0.0.1:3000/getProformatDetailWithoutBonLivraison/"+params.idProformat, content)
        .then((response)=>{
            return response.json();
        })
        .then((json)=>{
            setDetails(json.allProformatDetails)
            console.log(json.allWithBcmd);

            // filling the outQttInputs with false values so they are all hidden in the first place
            // let array = [json.allProformatDetails.length];
            // array.fill(false);
            // setOutQttInputs(array);
        })
    }, [])

    // Gestion erreur / loading
    if(details === undefined){
        return <p>Loading for fetching the data from server</p>
    }
    
    return(
        <Fragment>
            <Header/>
            <table border='1'>
                <thead>
                    <tr>
                        <th>Qtt en stock disponible</th>
                        <th>Qtt sortie</th>
                        <th>Nom article</th>
                        <th>Qualite</th>
                    </tr>
                </thead>
                <tbody>
                {details.map( (element,index) =>(
                    <tr key={index}>
                        <td>{element.qtt}</td>
                        <td>{element.out_qtt}</td>
                        <td>{element.nomarticle}</td>
                        <td>{element.qualite}</td>
                        <td><input type="checkbox" onChange={() =>  {generateInputField(index); }} ref={ el => (checkBoxesRefs.current[index] = el) } name="checkBoxes" /></td>
                        { checkBoxesRefs.current[index].checked ?  <td><input type="number" ref={ el => (outQttInputsRefs.current[index] = el) }  /></td> : '' }
                        { checkBoxesRefs.current[index].checked ?  <td><input type="date" ref={ el => (dateInputsRefs.current[index] = el) }  /></td> : '' }
                    </tr>
                ))}
                </tbody>
            </table>

            <button onClick={generateBonSortie}>Valider bon sortie</button>
        </Fragment>
    );
}
export default PFDBonLivraison;
