import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import Header from "../../../Template/Appro/Header";

const Facture = () => {
    const params = useParams();
    const [generalInfos, setGeneralInfos] = useState();
    const [allDetails, setAllDetails] = useState([]);
    const [today, setToday] = useState();

    useEffect(() => {
        let content = {
          method: "GET",
        };
    
        fetch("http://127.0.0.1:3000/getFacturePreview/" +params.idProformat,content)
          .then((response) => {
            return response.json();
          })
          .then((json) => {
              console.log(json);
            setGeneralInfos(json.generalInfos);
            setAllDetails(json.allDetails);
            // setCorporateInfos(json.corporateInfos);
    
            let date = new Date();
            let bref =
              date.getFullYear() +
              "-" +
              (date.getMonth() + 1) +
              "-" +
              date.getDate();
            setToday(bref);
          });
      }, []);

      const saveFacture = () => {

      }


    return(
        <>
            <Header  />
            <div className="container">
        <div className="row justify-content-center">
          <h3>Aperçu de facture</h3>
          <hr />
        </div>

        <div className="row">
          <div className="col-lg">
            <h5>
              <strong>Nom de la Societe: </strong>
              {/* {corporateInfos.nom} */}
            </h5>
            <h5>
              <strong>Adresse: </strong>
              {/* {corporateInfos.adresse} */}
            </h5>
            <h5>
              <strong>Telephone: </strong>
              {/* {corporateInfos.tel} */}
            </h5>
          </div>
        </div>

        <div className="row"></div>

        <div className="row align-items-center justify-content-center">
          <div className="col-lg-4">
            <h5>
              <strong>Date aujourd'hui: </strong>
              {today}
            </h5>
          </div>
          <div className="col-lg-4"></div>
          <div className="col-lg-4">
            <h5>
              {/* <strong>Nom du Client: </strong> {corporateInfos.nom_employe} */}
            </h5>
          </div>
        </div>

        <div className="">
          <div className="row justify-content-center align-items-center h-100">
            <div className="col-lg">
              <div className="row">
                <table className="table" border="1">
                  <thead>
                    <tr>
                      <th>Nom article</th>
                      <th>Quantite</th>
                      <th>PU</th>
                      <th>Montant ht</th>
                      <th>Montant TVA</th>
                      <th>Montant TTC</th>
                    </tr>
                  </thead>

                  <tbody>
                    {allDetails.map((detail, index) => (
                      <tr key={index}>
                        <td>{detail.nomarticle}</td>
                        <td>{detail.qtt_livre}</td>
                        <td>{detail.pu}</td>
                        <td>{detail.ht}</td>
                        <td>{detail.tva}</td>
                        <td>{detail.ttc}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-lg">
            <h5>
              <strong>Mode de Livraison: </strong>
              {/* {corporateInfos.nommode_livraison} */}
            </h5>
            <h5>
              <strong>Date de Livraison: </strong>
              {/* {corporateInfos.date_livraison} */}
            </h5>
          </div>
        </div>

        <hr />

        <div class="row">
            <div class="col-lg">
                <h5><strong>Montant total HT:</strong>{generalInfos.sum_ht}</h5>
                <h5><strong>Montant total TVA:</strong>{generalInfos.sum_tva}</h5>
                <h5><strong>Montant total TTC:</strong>{generalInfos.sum_ttc}</h5>
                <h5><strong>Montant en lettre:</strong>{generalInfos.sum_in_letter}</h5>
            </div>
        </div>

        <button onClick={saveFacture}>Générer</button>
      </div>

        </>
    );
}
export default Facture;
