import { useEffect ,useState} from "react"
const HistoriqueStock = () => {
    const [allDetails, setAllDetails] = useState([]);
    useEffect(() => {
        let content = {
            method: "GET",
          };
      
          fetch("http://127.0.0.1:3000/getAllTransaction",content)
          .then( (response) => {
                return response.json()
          })
          .then( (json) => {
              console.log(json.allDetails);
                setAllDetails(json.allDetails)
          })
    }, [])
    

    if(allDetails===undefined)
        return <p>Loading</p>

    return(
        <>
            <table border={1}>
                <thead>
                    <tr>
                        <th>Nom article</th>
                        <th>Qtt</th>
                        <th>Prix unitaire</th>
                    </tr>    
                </thead>   

                <tbody>
                    {allDetails.map( (element,index) =>  (
                    <tr>
                        <td>Savon</td>
                        <td>2</td>
                        <td>2000</td>
                    </tr>
                    ))}
                </tbody>
            </table>
        </>
    );
}

export default HistoriqueStock;
