import { useEffect } from "react";
import { useState } from "react";

const Analyse = () => {
  const filtre = [
    { id: 1, value: "Fournisseur" },
    { id: 2, value: "Produit" },
    { id: 3, value: "Par semaine" },
    { id: 4, value: "Par mois" },
  ];
  const [critere, setCritere] = useState(null);
  const [data, setData] = useState([]);

  useEffect(() => {
    var content = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    };

    content.body = JSON.stringify(filtre[0]);
    fetch("http://127.0.0.1:3000/getdata_analyse", content)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        setData(json.data);
      });
  }, []);

  useEffect(() => {
    if (critere != null) {
      var value = filtre.filter((e) => e.id === parseInt(critere))[0];

      var content = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
      };

      content.body = JSON.stringify(value);
      fetch("http://127.0.0.1:3000/getdata_analyse", content)
        .then((response) => {
          return response.json();
        })
        .then((json) => {
          setData(json.data);
        });
    }
  }, [critere]);

  return (
    <div>
      <h1>Analyse chiffre d'affaire sur l'achat</h1>
      <br />

      <h2>Filtre selon critere</h2>
      <div className="col-sm-2">
        <select
          className="form-control"
          onClick={(e) => setCritere(e.target.value)}
        >
          {filtre.map((e, index) => (
            <option key={index} value={e.id}>
              {e.value}
            </option>
          ))}
        </select>
      </div>

      <div>
        <table className="table">
          <thead>
            <tr>
              <th>Designation</th>
              <th>Chiffre d'affaire</th>
            </tr>
          </thead>
          <tbody>
            {data.map((datas, index) => (
              <tr key={index}>
                <td>{datas.critere}</td>
                <td>{datas.total} Ar</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Analyse;
