import { useState,useEffect } from "react";
import {Link} from "react-router-dom";

function ListDemandeProforma() {

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch("http://127.0.0.1:3000/demandeproforma")
      .then(res => res.json())
      .then(
        (result) => {

          setIsLoaded(true);
          setItems(result);

        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <div>

      <table border="2">
        <thead>
          <th>Nom de l'entreprise fournisseur</th>
          <th>Date demande</th>
          <th>Adresse de livraison</th>
        </thead>
        <tbody>
            {
              items.map( (element,index) => (
                <tr key={index}>
                  <td>{element.nom}</td>
                  <td>{element.date_demande}</td>
                  <td>{element.adresse}</td>
                  <td><Link to={'/proforma/'+element.iddemande_proformat}>noter</Link></td>
                </tr>
              ))
            }
        </tbody>
      </table>

      </div>
    );
  }
}

//export default App;
export default ListDemandeProforma;
