import React, { useEffect, useState } from "react";

const Card = ({ index, rubrique, produit, quantite, unite, description }) => {
  const [listrubrique, setListrubrique] = useState([]);
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    var content = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    };

    fetch("http://127.0.0.1:3000/getData", content)
      .then((response) => {
        if (response.status === 400) {
          alert("Mot de passe non identifier");
        } else return response.json();
      })
      .then((json) => {
        setListrubrique(json.rubriques);
        setArticles(json.acrticles);
      });
  }, []);

  return (
    <div className="col-lg-3">
      <div className="card-service wow fadeInUp">
        <div className="header">
          <img src="../assets/img/services/service-1.svg" alt="" />
        </div>
        <div className="body">
          <label htmlFor="produit">Rubrique</label>
          <select
            className="form-control"
            id="produit"
            name="produit"
            ref={(element) => {
              rubrique.current[index] = element;
            }}
          >
            {articles.map((article) => (
              <option value={article.idarticle}>{article.nom_article}</option>
            ))}
          </select>

          <label htmlFor="produit">Produit</label>
          <select
            className="form-control"
            id="produit"
            name="produit"
            ref={(element) => {
              produit.current[index] = element;
            }}
          >
            {listrubrique.map((rubriques) => (
              <option value={rubriques.idrubrique}>
                {rubriques.nomrubrique}
              </option>
            ))}
          </select>

          <label htmlFor="quantite">Quantite</label>
          <input
            className="form-control"
            id="quantite"
            type="number"
            name="quantite"
            ref={(element) => {
              quantite.current[index] = element;
            }}
          />

          <label htmlFor="unite">Unite</label>
          <input
            className="form-control"
            id="unite"
            type="number"
            name="unite"
            ref={(element) => {
              unite.current[index] = element;
            }}
          />

          <label htmlFor="description">Description</label>
          <input
            type="textArea"
            className="form-control"
            id="description"
            name="description"
            ref={(element) => {
              description.current[index] = element;
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default Card;
