import { useState,useEffect } from "react";
import {Link} from "react-router-dom";

function ListDemandeProformaBDC() {

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch("http://127.0.0.1:3000/demandeproforma")
      .then(res => res.json())
      .then(
        (result) => {

          setIsLoaded(true);
          setItems(result);

        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    //const fruits = ["Banana", "Orange", "Apple", "Mango"];
    //const fruits =   "{\"items\": [{ \"id\": 1, \"name\": \"Apples\",  \"price\": \"$2\" },{ \"id\": 2, \"name\": \"Peaches\", \"price\": \"$5\" }] }";
    return (
      <div>
      <ul>
      
      { items.map( d =>(<li key={d.iddemande_proformat}>
        <Link to={'/bondecommande/'+d.iddemande_proformat}>{d.nom}</Link> {d.date_demande}     
      </li>))}

      </ul>
      </div>
    );
  }
}

//export default App;
export default ListDemandeProformaBDC;
