import { Fragment ,useState,useEffect, useRef} from "react";
import { useNavigate, useParams } from "react-router";
import Header from "../../../Template/Appro/Header";

const PFDBonLivraison = () => {
    const navigate = useNavigate();
    const params = useParams();
    const [details, setDetails] = useState([]);
    const [indicator,setIndicator] = useState([]);
    
    const checkBoxesRefs = useRef([]);
    const dateInputsRefs = useRef([]);
    const outQttInputsRefs = useRef([]);

    class BonLivraison{
        constructor(a,b,c){
            this.details_bon_commande_fournisseur_id = a;
            this.qtt_livre = b;
            this.date_livraison = c;
        }

        details_bon_commande_fournisseur_id;
        out_qtt;
        date_livraison;
    }


    //     # Il faut savoir que la qtt qu'il essaye de sortir ne dépasse pas le stock
    const generateBonLivraison = () => {
        let bon_livraison_arrays = [];
        checkBoxesRefs.current.map( (element,index) => {
            if(element.checked)
            bon_livraison_arrays.push(new BonLivraison(details[index].iddetails_bon_commande_fournisseur,+outQttInputsRefs.current[index].value),dateInputsRefs.current[index].value)
        });
        
        let content = {
            method : "POST",
            headers : {
                "Content-Type": "application/json"
            },
        };

        content.body = JSON.stringify(bon_livraison_arrays);
        
        fetch("http://127.0.0.1:3000/generateBonSortie",content)
        .then(response => {
            console.log(response.status);
            if(response.status!==200){
                throw new Error(response);
            }

            else if(response.status===200)
                return response.json();
        })
        .then(data => {
            navigate('/menu');  
        })
        .catch(error => {
            alert("La quantite que vous essayez de sortir est superieur a la commande")
        })
    }
    
    useEffect(() => {
        let content = {
            method : "GET",
            headers : {
                "Content-Type": "application/json"
            },
        };
  
        fetch("http://127.0.0.1:3000/getProformatDetailWithoutBonLivraison/"+params.idProformat, content)
        .then((response)=>{
            return response.json();
        })
        .then((json)=>{
            setDetails(json.allProformatDetails)
            console.log(json.allProformatDetails);

            // filling the outQttInputs with false values so they are all hidden in the first place
            let array = [json.allProformatDetails.length];
            // array.fill(false);
            // setIndicator(array);

            json.allProformatDetails.map( (element,index) => {
                array[index]= <td><input type="checkbox" ref={ el => (checkBoxesRefs.current[index] = el) } name="checkBoxes" /></td>;
            });
            setIndicator(array);
        })
    }, [])

    const changeVisibility = (index) =>{
        let indicator_copy = [...indicator];
        indicator_copy[index] = !indicator_copy[index] ;
        setIndicator(indicator_copy);
    }

    // Gestion erreur / loading
    if(details === undefined){
        return <p>Loading for fetching the data from server</p>
    }
    
    return(
        <Fragment>
            <Header/>
            <table border='1'>
                <thead>
                    <tr>
                        <th>Qtt en stock disponible</th>
                        <th>Qtt sortie</th>
                        <th>Nom article</th>
                        <th>Qualite</th>
                    </tr>
                </thead>
                <tbody>
                {details.map( (element,index) =>(
                    <tr key={index}>
                        <td>{element.qtt}</td>
                        <td>{element.out_qtt}</td>
                        <td>{element.nomarticle}</td>
                        <td>{element.qualite}</td>
                        {indicator[index]}
                        {/* { indicator[index] ?  <td><input type="number" ref={ el => (outQttInputsRefs.current[index] = el) }  /></td> : '' }
                        { indicator[index] ?  <td><input type="date" ref={ el => (dateInputsRefs.current[index] = el) }  /></td> : '' } */}
                    </tr>
                ))}
                </tbody>
            </table>

            <button onClick={generateBonLivraison}>Valider bon sortie</button>
        </Fragment>
    );
}
export default PFDBonLivraison;
