import React, { useRef, useState,useEffect } from 'react';
import { useParams } from 'react-router';
import Header from '../../Template/Appro/Header';

var id_proforma = 1;

function DropDown(props){


  const options = props.options;
  const listItems = options.map((option,index) =>
    <option key={index} value={option.value}>{option.text}</option>
  );
  return (
    
    <select className="form-control" ref={props.textinput}>{listItems}</select>
  );
}

function ProformaHeader(props) {
  const {iddemande} = useParams();
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  useEffect(() => {
    
    fetch("http://127.0.0.1:3000/fournisseurproformaM?iddemande="+iddemande)
      .then(res => res.json())
      .then(
        (result) => {

          setIsLoaded(true);
          setItems(result);

        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])
 
 var fournisseurs = [];
  items.map((element)=>{
    const line = {"value":element.idfournisseur,"text":element.nom};
    fournisseurs.push(line);
  })


  var data_modeLivraison=[
    {label: "livraison en point relais",value: 1},
    {label: "livraison express",value: 2}
  ]
//console.log(items);
    return( 
      <div>
      <Header/>
        <div className="col-md-6 col-sm-12 col-xs-12">
          <div className="x_panel">
              <div className="x_title">
                    <h2>Proforma <small>en tête</small></h2>
                    <div className="clearfix"></div>
                    <div className="x_content">
                      <form className="form-horizontal form-label-left">
                          <div className="form-group">
                            <div className="col-sm-9">
                              <div className="input-group">
                              <label className="col-sm-3 control-label">reference</label>
                                <input type="text" className="form-control" ref = {props.reference}/>
                              </div>
                              <div className="input-group">
                              <label className="col-sm-3 control-label">fournisseur</label>
                                <DropDown options={fournisseurs} textinput = {props.fournisseur}/>
                              </div>
                              <div className="input-group">
                                <label className="col-sm-3 control-label">info livraison</label>

                                <select
                                  className="form-control"
