import React, { useEffect, useState } from "react";
import Header from "../../Template/Appro/Header";
import ProformatFournisseur from "../proforma/ProformatFournisseur";

const ProformatHavingBcmd = () => {
    const [allProformat, setAllProformat] = useState([]);


    //    Get all proformat which have bon commande inside its details
    useEffect(() => {
      var content = {
          method : "GET",
          headers : {
              "Content-Type": "application/json"
          },
      };

      fetch("http://127.0.0.1:3000/getProformatWithBonCommande", content)
      .then((response)=>{
          return response.json();
      })
      .then((json)=>{
          setAllProformat(json.allProformat)
          console.log(allProformat);
      })
    }, [])
    

    return(
        <React.Fragment>
            <Header />
            {allProformat.map( (element,index) =>(
                <div className="container">
                    <ProformatFournisseur key={index} details={element} />
                    
                </div>
            ))}
        </React.Fragment>
    );
}
export default ProformatHavingBcmd;
