import { Route, Routes } from "react-router";
import { BrowserRouter } from "react-router-dom";
import AdminLogin from "./View/Page/Admin/AdminLogin";
import EmployeLogin from "./View/Page/Employe/EmployeLogin";
import DemandeAchat from "./View/Page/Employe/DemandeAchat";
import HomeApprovisionnement from "./View/Page/Admin/HomeApprovisionnement";
import ValidationDemandeAchat from "./View/Page/Admin/ValidationDemandeAchat";
import CreateProforma from "./View/Page/Admin/CreateProforma";
import BonCommande from "./View/Page/Admin/BonCommande";
import Menu from "./View/Page/Menu";
import "./App.css";
import Home from "./View/Page/Employe/Home";
import ProformaForm from "./View/Page/proforma/Proforma";
import ListDemandeProforma from "./View/Page/proforma/Demande_proforma";
import ListDemandeProformaBDC from "./View/Page/bon_commande/Demande_proforma";
import ListBondeCommande from "./View/Page/bon_commande/ListBondeCommande";

// import ListDemandeProforma from "./View/Page/proforma/Demande_proforma";

import Envoye_demande from "./View/Page/demandeProforma/envoye_demande";
import EmailInterface from "./View/Page/Admin/EmailInterface";

// FOURNISSEUR RELATED IMPORT
import ProformatHavingBcmd from "./View/Page/Fournisseur/ProformatHavingBcmd";
import ProformatDetailsBcmd from "./View/Page/Fournisseur/ProformatDetailsBcmd";

// PLAYFIELD RELATED URL
// import Give from "./View/Page/Playfield/Give";
// import Take from "./View/Page/Playfield/Take";
// import CheckBoxes from "./View/Page/Playfield/CheckBoxex";


function App() {
  return (
    <div
      id="main-wrapper"
      data-theme="light"
      data-layout="vertical"
      data-navbarbg="skin6"
      data-sidebartype="full"
      data-sidebar-position="fixed"
      data-header-position="fixed"
      data-boxed-layout="full"
    >
      <BrowserRouter>
        <Routes>
          <Route path="/menu" element={<Menu />} />
          <Route path="/loginadmin" element={<AdminLogin />}></Route>
          <Route path="/loginemploye" element={<EmployeLogin />}></Route>
          <Route path="proforma/:iddemande" element={<ProformaForm/>} ></Route>
          <Route path="/proforma" element={<ProformaForm/>} ></Route>
          <Route path="/listdemandeproforma" element={<ListDemandeProforma/>} ></Route>
          <Route path="/listdemandeproformabdc" element={<ListDemandeProformaBDC/>} ></Route>
          <Route path="bondecommande/:iddemande" element={<ListBondeCommande/>} ></Route>
          <Route path="/homeEmploye" element={<Home />}></Route>
          <Route path="/demandeAchat" element={<DemandeAchat />}></Route>
          <Route path="/" element={<ListDemandeProformatClient />}></Route>
          <Route
            path="/homeApprovisionnement"
            element={<HomeApprovisionnement />}
          ></Route>
          <Route
            path="/validationDemandeAchat"
            element={<ValidationDemandeAchat />}
          ></Route>
          <Route path="/createProforma" element={<CreateProforma />}></Route>
          <Route path="/bonCommande" element={<BonCommande />}></Route>
          <Route path="/demande_proforma" element={<Envoye_demande />}></Route>
          <Route path="/email_interface" element={<EmailInterface />}></Route>
          
          {/* FOURNISSEUR RELATED URL */}
          <Route path="/pfBcmd" element={<ProformatHavingBcmd />}></Route>
          <Route path="/pfBcmd/:idProformat" element={<ProformatDetailsBcmd />}></Route>

          {/* PLAYFIELD RELATED ROUTES */}
          {/* <Route path="/checkBox" element={<CheckBoxes />} /> */}
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
