import { Fragment ,useState,useEffect, useRef} from "react";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import Header from "../../Template/Appro/Header";

const ProformatDetailsBcmd = () => {
    const params = useParams();
    const [details, setDetails] = useState([]);
    const [outQttInputs,setOutQttInputs] = useState([]);
    
    const checkBoxesRefs = useRef([]);
    const outQttInputsRefs = useRef([]);

    class BonSortie{
        details_bon_commande_fournisseur_id;
        out_qtt;
    }

    const generateBonSortie = () => {
        let bon_sortie_arrays = [];
        console.log(b);
        checkBoxesRefs.map( (element,index) => {
            if(element.current.checked)
                bon_sortie_arrays.push(new BonSortie(details[index].iddetails_bon_commande_fournisseur,outQttInputsRefs.current[index].value))
        });
        console.log(bon_sortie_arrays);
    }

    const generateInputField =(index) => {
        let copie_outQttInputs = [...outQttInputs];
        copie_outQttInputs[index] = !copie_outQttInputs[index];
        setOutQttInputs(copie_outQttInputs)
    }
    
    useEffect(() => {
        let content = {
            method : "GET",
            headers : {
                "Content-Type": "application/json"
            },
        };
  
        fetch("http://127.0.0.1:3000/getDpfBcmd/"+params.idProformat, content)
        .then((response)=>{
            return response.json();
        })
        .then((json)=>{
            setDetails(json.allWithBcmd)
            
            // filling the outQttInputs with false values so they are all hidden in the first place
            let array = [json.allWithBcmd.length];
            array.fill(false);
            setOutQttInputs(array);
        })
    }, [])

    // Gestion erreur / loading
    if(details === undefined){
        return <p>Loading for fetching the data from server</p>
    }
    
    return(
        <Fragment>
            <Header/>
            <table border='1'>
                <thead>
                    <tr>
                        <th>Qtt</th>
                        <th>Prix avec augmentation 30%</th>
                        <th>Qualite</th>
                        <th>Montant HT</th>
                        <th>Montant TVA</th>
                        <th>Montant TTC</th>
                        <th>Quantite a sortir</th>
                    </tr>
                </thead>
                <tbody>
                {details.map( (element,index) =>(
                    <tr key={index}>
                        <td>{element.qtt}</td>
                        <td>{element.pu_30}</td>
                        <td>{element.qualite}</td>
                        <td>{element.ht}</td>
                        <td>{element.tva}</td>
                        <td>{element.ttc}</td>
                        { outQttInputs[index] ?  <td><input type="number" ref={ el => (outQttInputsRefs.current[index] = el) }  /></td> : '' }
                        <td><input type="checkbox" onChange={() =>  {generateInputField(index); }} ref={ el => (checkBoxesRefs.current[index] = el) } name="checkBoxes" /></td>
                    </tr>
                ))}
                </tbody>
            </table>

            <button onClick={generateBonSortie}>Valider bon sortie</button>
        </Fragment>
    );
}

export default ProformatDetailsBcmd;
