import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Give = () =>{
    const [allEmployes,setAllEmployes] = useState([])

    const navigate = useNavigate();

    const toComponentTake=(element)=>{
        navigate('/take',{state:{element}});
    }

    useEffect(() => {    
        let content = {
            method: "GET",
        };

    // TODO fixed the data of bon de commande to 1
        fetch("http://127.0.0.1:3000/give", content)
        .then((response) => {
            return response.json();
        })
        .then((json) => {
            setAllEmployes(json)
        })
    },[]);

    return(
        <div id="indro">
            { allEmployes.map(employe=>(
                <p>
                    <Link to={{
                    pathname:'/take',
                    state:{employe}
                    }} >{employe.email}</Link>
                </p>
            ))
            }
        </div>
    );

}

export default Give;
