import React, { useState } from "react";
import DataList from "./DataList";

const Card = ({
  index,
  rubrique,
  quantite,
  unite,
  description,
  nomarticle,
  listrubrique,
  articles,
  unites,
}) => {
  const [idrubrique,setIdRubrique] = useState("")

  const handleClick=(event) =>{

    setIdRubrique(event.target.value)
    // console.log(idrubrique);
  }

  return (
    <div className="col-lg-3">
      <div className="card-service wow fadeInUp">
        <div className="header">
          <img src="../assets/img/services/service-1.svg" alt="" />
        </div>

        <div className="body">
          <label htmlFor="produit">Rubrique</label>
          <select
            className="form-control"
            id="produit"
            name="produit"
            ref={(element) => {
              rubrique.current[index] = element;
            }}
            
            onChange={handleClick}
          >
            {listrubrique.map((rubriques, index) => (
              <option value={rubriques.idrubrique} key={index} >
                {rubriques.nomrubrique}
              </option>
            ))}
          </select>

          <DataList data={articles} rubrique={idrubrique} index={index} nomarticle={nomarticle} />

          <label htmlFor="quantite">Quantite</label>
          <input
            className="form-control"
            id="quantite"
            type="number"
            name="quantite"
            ref={(element) => {
              quantite.current[index] = element;
            }}
          />

          <label htmlFor="unite">Unite</label>
          <select
            className="form-control"
            id="produit"
            name="produit"
            ref={(element) => {
              unite.current[index] = element;
            }}
          >
            {unites.map((unite, index) => (
              <option value={unite.idunite} key={index}>
                {unite.nomunite}
              </option>
            ))}
          </select>

          <label htmlFor="description">Description</label>
          <input
            type="textArea"
            className="form-control"
            id="description"
            name="description"
            ref={(element) => {
              description.current[index] = element;
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default Card;
