import { useState,useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {Link} from "react-router-dom";
import Header from "../../Template/Appro/Header";


function Bonton(props){
  const navigate=useNavigate()

  function change_state(etat,idbc){
    let state = 0;
    if(etat==0){
      state = etat;
      console.log("refuser");
      
        }else{
          state = 10;
      console.log("accepter");
    
    }
    fetch("http://127.0.0.1:3000/change_etat?etat="+state+"&idbdc="+idbc)
    
  }

  const handleClick=()=>{
    var content={
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    }

    fetch( "http://127.0.0.1:3000/saveBonReception/" +props.idbc,content)
    navigate('/validationReception',{state:{idboncommande:props.idbc}})
  }

  if(props.etat<0){
    return (<div>
          <p>refusée</p>  
    </div>);
  }
  else if (props.etat==0) {
    return (
      <div>

      <button className="btn btn-danger" onClick={()=>change_state(-1,props.idbc)}>refuser</button>
      <button className="btn btn-primary" onClick={()=> change_state(1,props.idbc)}>accepter</button>
      </div>    
    );    
  }else{
    return (
      <div>
      <button className="btn btn-success"  onClick={handleClick}>générer bon de récéption</button>
      </div>        
    );
  }

}


function ListBondeCommande() {
  const {iddemande} = useParams();
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch("http://127.0.0.1:3000/list_bon_de_commande?iddemande="+iddemande)
      .then(res => res.json())
      .then(
        (result) => {

          setIsLoaded(true);
          setItems(result);

        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    //const fruits = ["Banana", "Orange", "Apple", "Mango"];
    //const fruits =   "{\"items\": [{ \"id\": 1, \"name\": \"Apples\",  \"price\": \"$2\" },{ \"id\": 2, \"name\": \"Peaches\", \"price\": \"$5\" }] }";
    return (
      <div className="container">
        <Header/>
      <table className="table table-dark table-borderless">
        <thead>
          <tr>
          <th>idbon_commande</th>
            <th>date_creation</th>
            <th>montant hors taxe</th>
            <th>montant TVA</th>
            <th>montant TTC</th>
            <th>etat</th>
        
          </tr>
        </thead>

        <tbody>
          { items.map( d =>(
          <tr key={d.idbon_commande}> 
          <td>{d.idbon_commande}</td>
          <td>{d.date_creation}</td> 
          <td>{d.montant_total_ht}</td>
          <td>{d.montant_total_tva}</td>
          <td>{d.montant_total_ttc}</td>
          <td><Bonton etat={d.etat} idbc={d.idbon_commande}/></td>
          
          </tr> 
          ))}
      </tbody>

      </table>
      </div>
    );
  }
}

//export default App;
export default ListBondeCommande;
