import { useEffect ,useState} from "react"
const HistoriqueStock = () => {
    const [allDetails, setAllDetails] = useState([]);
    const [currentState, setCurrentState] = useState([]);
    useEffect(() => {
        let content = {
            method: "GET",
          };
      
          fetch("http://127.0.0.1:3000/getAllTransaction",content)
          .then( (response) => {
                return response.json()
          })
          .then( (json) => {
              console.log(json.allDetails);
                setAllDetails(json.allDetails)
          })
    }, [])
    

    if(allDetails===undefined)
        return <p>Loading</p>

    return(
        <>
            <h2>Historique des transactions</h2>
            <table border={1}>
                <thead>
                    <tr>
                        <th>Nom article</th>
                        <th>Qtt</th>
                        <th>Prix unitaire</th>
                    </tr>    
                </thead>   

                <tbody>
                    {allDetails.map( (element,index) =>  (
                    <tr key={index}>
                        <td>{element.nomarticle}</td>
                        <td>{element.out_qtt}</td>
                        <td>{element.pu}</td>
                    </tr>
                    ))}
                </tbody>
            </table>

            <h2>Stock actuel</h2>
            <table border={1}>
                <thead>
                    <tr>
                        <th>Nom article</th>
                        <th>Qtt</th>
                        <th>Prix unitaire</th>
                        <th>MÃ©thode de transaction</th>
                    </tr>    
                </thead>   
                
                <tbody>
                    {currentState.map( (element,index) => (
                        <tr key={index}>
                        <td>{element.nomarticle}</td>
                        <td>{element.remaining_qtt}</td>
                        <td>{element.pu}</td>
                    </tr>
                    ))}
                </tbody>
            </table>
        </>
    );
}

export default HistoriqueStock;
