import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router";

// just the table first
const BonCommande = (props) =>{
    const params = useParams();
    const [bdcDetails,setBdcDetails] = useState([])
    const [bdcGenerals, setBdcGenerals] = useState([])
    const [corporateInfos, setCorporateInfos] = useState([])
    const [today,setToday] = useState([])
    const [bonCommandeSelf,setBonCommandeSelf] = useState([])

    const saveBonCommande = (event)=>{
        let content = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
        };
        content.data = JSON.stringify(bonCommandeSelf);
        fetch("http://127.0.0.1:3000/insertBonDeCommande", content)
    }

    useEffect(() => {    
        let content = {
            method: "GET",
        };

    // TODO fixed the data of bon de commande to 1
        fetch("http://127.0.0.1:3000/giveDataBonDeCommande/"+params.idDemandeProformat, content)
        .then((response) => {
            return response.json();
        })
        .then((json) => {
            setBdcGenerals(json.bdcGenerals);
            setBdcDetails(json.bdcDetails);
            setCorporateInfos(json.corporateInfos);

            let date = new Date();
            let bref = date.getFullYear()+'-'+(date.getMonth()+1)+'-'+date.getDate()
            setToday(bref);

            setBonCommandeSelf(json.bdcGenerals);
        })
    },[]);
 
    return (
        <Fragment>
            <div class="container">
        

        <div class="row justify-content-center">
        <h3>Aperçu de Bon de Commande</h3>
        <hr/>
        </div>


        <div class="row">
        <div class="col-lg">
            <h5><strong>Nom de la Societe: </strong>{corporateInfos.nom}</h5>
            <h5><strong>Adresse: </strong>{corporateInfos.adresse}</h5>
            <h5><strong>Telephone: </strong>{corporateInfos.tel}</h5>
        </div>
        </div>

        <div class="row">
        </div>

        <div class="row align-items-center justify-content-center">
        <div class="col-lg-4">
            <h5><strong>Date aujourd'hui: </strong>{today}</h5>
        </div>
        <div class="col-lg-4">
        </div>
        <div class="col-lg-4">
            <h5><strong>Nom du Client: </strong> {corporateInfos.nom_employe}</h5>
        </div>

        </div>

        <div class="">
        <div class="row justify-content-center align-items-center h-100">
            <div class="col-lg">
            <div class="row">
                <table class="table" border="1">
                    <thead>
                        <th>Nom article</th>
                        <th>Quantite</th>
                        <th>PU</th>
                        <th>Montant ht</th>
                        <th>Montant TVA</th>
                        <th>Montant TTC</th>
                    </thead>

                    <tbody>
                        {bdcDetails.map((bdcDetail) => (
                            <tr>
                                <td>{bdcDetail.nom_article}</td>
                                <td>{bdcDetail.qtt}</td>
                                <td>{bdcDetail.pu}</td>
                                <td>{bdcDetail.montantht}</td>
                                <td>{bdcDetail.montanttva}</td>
                                <td>{bdcDetail.montantttc}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            </div>
        </div>
        </div>

        <div class="row">
            <div class="col-lg">
                <h5><strong>Mode de Livraison: </strong>{corporateInfos.nommode_livraison}</h5>
                <h5><strong>Date de Livraison: </strong>{corporateInfos.date_livraison}</h5>
            </div>
        </div>

        <hr/>

        <div class="row">
            <div class="col-lg">
                <h5><strong>Montant total HT:</strong>{bdcGenerals.montant_total_ht}</h5>
                <h5><strong>Montant total TVA:</strong>{bdcGenerals.montant_total_tva}</h5>
                <h5><strong>Montant total TTC:</strong>{bdcGenerals.montant_total_ttc}</h5>
                <h5><strong>Montant en lettre:</strong>{bdcGenerals.montant_lettre}</h5>
            </div>
        </div>

        <button onClick={saveBonCommande}>Sauvegarder</button>

        </div>
        </Fragment>
  );
}

export default BonCommande;
